import React from "react";
import "./App.css";
import Card from "./Card.js";
import Pokemon from "./Pokemon.js";

const TRAINERS_URL = "http://localhost:3000/trainers";

class App extends React.Component {
  state = { data: [] };

  componentDidMount = () => {
    fetch(TRAINERS_URL)
      .then(resp => resp.json())
      .then(data => this.setState({ data }));
  };

  releasePokemon = (id) => {
    fetch(TRAINERS_URL + '/' + id, {
      method: 'DELETE'
    })
  }

  render() {
    return (
      <div>
        <header>
          <h2>Pokemon Teams!</h2>
        </header>
        <main>
          {this.state.data.map(trainer => {
            return <Card trainer={trainer} key={trainer.id} />;
          })}
        </main>
      </div>
    );
  }
}

export default App;
