  render() {
    return (
      <div>
        <header>
          <h2>Pokemon Teams!</h2>
        </header>
        <main>
          {this.state.data.map(trainer => {
            return (
              <Card
                trainer={trainer}
                key={trainer.id}
                releasePokemon={this.releasePokemon}
              />
            );
          })}
        </main>
      </div>
    );
  }
}

export default App;
